from nodes.base_node import BaseNode
from typing import Dict, Any

class TextProcessorNode(BaseNode):
    def __init__(self, operation: str = 'uppercase', **kwargs):
        super().__init__(
            name=f'Text {operation.title()}',
            type='text_processor',
            description=f'Process text with {operation} operation',
            **kwargs
        )
        self.operation = operation

    async def execute(self, context: Dict[str, Any]) -> Dict[str, Any]:
        text = self.inputs.get('text', '')
        if not isinstance(text, str):
            text = str(text)

        if self.operation == 'uppercase':
            result = text.upper()
        elif self.operation == 'lowercase':
            result = text.lower()
        elif self.operation == 'capitalize':
            result = text.capitalize()
        elif self.operation == 'reverse':
            result = text[::-1]
        elif self.operation == 'length':
            result = len(text)
        else:
            result = text

        self.mark_completed({'processed_text': result})
        return {'processed_text': result}

def create_text_processor_node(**kwargs):
    operation = kwargs.get('operation', 'uppercase')
    return TextProcessorNode(operation=operation, **kwargs)

from nodes.base_node import BaseNode
from typing import Dict, Any, List

class DataValidatorNode(BaseNode):
    def __init__(self, rules: List[Dict[str, Any]] = None, **kwargs):
        super().__init__(
            name='Data Validator',
            type='data_validator',
            description='Validate data against custom rules',
            **kwargs
        )
        self.rules = rules or []

    async def execute(self, context: Dict[str, Any]) -> Dict[str, Any]:
        data = self.inputs.get('data', {})
        if not isinstance(data, dict):
            self.mark_completed({'is_valid': False, 'errors': ['Input must be an object']})
            return {'is_valid': False, 'errors': ['Input must be an object']}

        errors = []

        for rule in self.rules:
            field = rule.get('field')
            if not field:
                continue

            value = data.get(field)

            # Check required
            if rule.get('required', False) and (field not in data or value is None):
                errors.append(f'Field {field} is required')
                continue

            if value is None:
                continue

            # Type validation
            expected_type = rule.get('type')
            if expected_type:
                if expected_type == 'string' and not isinstance(value, str):
                    errors.append(f'Field {field} must be a string')
                elif expected_type == 'number' and not isinstance(value, (int, float)):
                    errors.append(f'Field {field} must be a number')
                elif expected_type == 'boolean' and not isinstance(value, bool):
                    errors.append(f'Field {field} must be a boolean')
                elif expected_type == 'array' and not isinstance(value, list):
                    errors.append(f'Field {field} must be an array')
                elif expected_type == 'object' and not isinstance(value, dict):
                    errors.append(f'Field {field} must be an object')

            # Length validation for strings
            if isinstance(value, str):
                min_len = rule.get('min_length')
                max_len = rule.get('max_length')
                if min_len is not None and len(value) < min_len:
                    errors.append(f'Field {field} must be at least {min_len} characters')
                if max_len is not None and len(value) > max_len:
                    errors.append(f'Field {field} must be at most {max_len} characters')

                # Pattern validation
                pattern = rule.get('pattern')
                if pattern and not __import__('re').match(pattern, value):
                    errors.append(f'Field {field} does not match required pattern')

        is_valid = len(errors) == 0
        self.mark_completed({'is_valid': is_valid, 'errors': errors})
        return {'is_valid': is_valid, 'errors': errors}

def create_data_validator_node(**kwargs):
    rules = kwargs.get('rules', [])
    return DataValidatorNode(rules=rules, **kwargs)
